mapper.update:
UPDATE draft
SET draft_category=#{draft_category, jdbcType=INTEGER}, draft_content=#{draft_content, jdbcType=VARCHAR}
WHERE doc_no = #{doc_no}

mapper.select:
SELECT file_no, doc_no, save_filename, original_filename
FROM draft_file
WHERE doc_no = #{doc_no}

SELECT file_no, doc_no, save_filename, original_filename
FROM draft_file
WHERE file_no = #{file_no}
