mapper.select:
SELECT c.club_id, c.club_name, cb.club_board_no, e.emp_name, cb.board_category, cb.clubboard_title, cb.clubboard_content, TO_CHAR (cb.clubboard_regdate, 'YYYY-MM-DD') clubboard_regdate, cb.clubboard_hitcount FROM club_board cb JOIN club c ON cb.club_id=c.club_id JOIN emp e ON cb.emp_no=e.emp_no WHERE cb.board_category=#{board_category} AND cb.club_id=#{club_id}
AND
ORDER BY club_board_no DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY
