mapper.select:
SELECT c.club_id, c.club_name, NVL(memberCount,0) member_count, e.emp_name, TO_CHAR (c.club_startdate, 'YYYY-MM-DD') club_startdate FROM club c LEFT OUTER JOIN ( SELECT club_id, COUNT(*) memberCount FROM club_member GROUP BY club_id ) cmm ON c.club_id=cmm.club_id JOIN club_member cm ON c.club_id=cm.club_id JOIN emp e ON cm.emp_no=e.emp_no WHERE cm.emp_no=#{emp_no}
AND
ORDER BY club_id DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY
