mapper.select:
SELECT TRUNC((MONTHS_BETWEEN(TRUNC(SYSDATE), TRUNC(e.emp_join_date)) / 12 ) + 1) AS annual_leave
FROM emp e
WHERE e.emp_no = #{emp_no}

mapper.update:
UPDATE emp 
SET emp_email = #{emp_email},
	emp_tel = #{emp_tel}, emp_zip = #{emp_zip}, emp_addr1 = #{emp_addr1}, emp_addr2 = #{emp_addr2},
	emp_picture = #{emp_picture}
WHERE emp_no = #{emp_no}

mapper.select:
SELECT record_no, emp_no, TO_CHAR(work_starttime, 'MM/DD') work_day, TO_CHAR(work_starttime, 'HH24:MI') work_starttime, TO_CHAR(work_endtime, 'HH24:MI') work_endtime, work_status FROM worktime_recode
AND emp_no = #{emp_no}
AND TO_CHAR(work_starttime, 'YYYYMM') = #{work_starttime}
ORDER BY work_day
