mapper.update:
UPDATE research 
SET research_status = 0 
WHERE research_id=#{research_id}

mapper.select:
SELECT r.research_id, r.research_title, TO_CHAR(r.research_startdate,'YYYY-MM-DD') research_startdate, TO_CHAR(r.research_enddate,'YYYY-MM-DD') research_enddate, e.emp_name, TO_CHAR(r.research_regdate,'YYYY-MM-DD') research_regdate FROM research r JOIN emp e ON r.emp_no=e.emp_no WHERE e.emp_no=#{emp_no}
AND
ORDER BY research_id DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY
