mapper.select:
select meroom_id, meroom_name, meroom_info, meroom_status
FROM meetingroom
WHERE meroom_id=#{meroom_id}

SELECT m.meroom_id, m.meroom_name, m.meroom_info, mr.meroom_res_no, e.emp_name, TO_CHAR(mr.meroom_resdate,'YYYY-MM-DD') meroom_resdate, TO_CHAR(mr.res_starttime,'HH24:MI') res_starttime, TO_CHAR(mr.res_endtime ,'HH24:MI') res_endtime, mr.res_content FROM meetingroom_reservation mr JOIN meetingroom m ON m.meroom_id=mr.meroom_id JOIN emp e ON mr.emp_no=e.emp_no
e.emp_no=${emp_no}
AND TO_CHAR(mr.meroom_resdate,'YYYYMM')=#{meroom_resdate}
